mapper.select:
select * from PMAppplatform


		and id = #{id, jdbcType=BIGINT}


		and createdate = #{createdate, jdbcType=TIMESTAMP}


		and modifydate = #{modifydate, jdbcType=TIMESTAMP}


		and platform_no = #{platformNo, jdbcType=VARCHAR}


		and platform_name = #{platformName, jdbcType=VARCHAR}


		and domain = #{domain, jdbcType=VARCHAR}


		and status = #{status, jdbcType=CHAR}


		and settlement_interval = #{settlementInterval, jdbcType=INTEGER}


		and settlement_type = #{settlementType, jdbcType=CHAR}


		and settlement_bank = #{settlementBank, jdbcType=VARCHAR}


		and settlement_card = #{settlementCard, jdbcType=VARCHAR}


		and settlement_party = #{settlementParty, jdbcType=CHAR}


		and minimum_amount = #{minimumAmount, jdbcType=INTEGER}


		and maximum_amount = #{maximumAmount, jdbcType=INTEGER}

select id,createdate,modifydate,platform_no platformNo,platform_name platformName,domain,status,settlement_interval settlementInterval,
settlement_type settlementType,settlement_bank settlementBank,settlement_card settlementCard,settlement_party settlementParty,
minimum_amount minimumAmount,maximum_amount maximumAmount from PMAppplatform where id = #{id}

select count(1) from PMAppplatform


		and id = #{id, jdbcType=BIGINT}


		and createdate = #{createdate, jdbcType=TIMESTAMP}


		and modifydate = #{modifydate, jdbcType=TIMESTAMP}


		and platform_no = #{platformNo, jdbcType=VARCHAR}


		and platform_name = #{platformName, jdbcType=VARCHAR}


		and domain = #{domain, jdbcType=VARCHAR}


		and status = #{status, jdbcType=CHAR}


		and settlement_interval = #{settlementInterval, jdbcType=INTEGER}


		and settlement_type = #{settlementType, jdbcType=CHAR}


		and settlement_bank = #{settlementBank, jdbcType=VARCHAR}


		and settlement_card = #{settlementCard, jdbcType=VARCHAR}


		and settlement_party = #{settlementParty, jdbcType=CHAR}


		and minimum_amount = #{minimumAmount, jdbcType=INTEGER}


		and maximum_amount = #{maximumAmount, jdbcType=INTEGER}

		select id,createdate,modifydate,platform_no platformNo,platform_name platformName,domain,status,settlement_interval settlementInterval,
settlement_type settlementType,settlement_bank settlementBank,settlement_card settlementCard,settlement_party settlementParty,
minimum_amount minimumAmount,maximum_amount maximumAmount from PMAppplatform


				and id = #{id, jdbcType=BIGINT}


				and createdate = #{createdate, jdbcType=TIMESTAMP}


				and modifydate = #{modifydate, jdbcType=TIMESTAMP}


				and platform_no = #{platformNo, jdbcType=VARCHAR}


				and platform_name = #{platformName, jdbcType=VARCHAR}


				and domain = #{domain, jdbcType=VARCHAR}


				and status = #{status, jdbcType=CHAR}


				and settlement_interval = #{settlementInterval, jdbcType=INTEGER}


				and settlement_type = #{settlementType, jdbcType=CHAR}


				and settlement_bank = #{settlementBank, jdbcType=VARCHAR}


				and settlement_card = #{settlementCard, jdbcType=VARCHAR}


				and settlement_party = #{settlementParty, jdbcType=CHAR}


				and minimum_amount = #{minimumAmount, jdbcType=INTEGER}


				and maximum_amount = #{maximumAmount, jdbcType=INTEGER}


		LIMIT 2

select id,platform_no platformNo,platform_name platformName,`status` from pmappplatform 

	status!='2'

		and platform_no = #{platformNo, jdbcType=VARCHAR}


		and platform_name like concat('%',#{platformName},'%')  



	order by ${sort}  ${order}


	order by createdate desc

limit #{offset},#{limit}

UPDATE pmappplatform SET  modifydate=sysdate(),platform_name=#{platformName,jdbcType=VARCHAR},
domain=#{domain,jdbcType=VARCHAR},status=#{status,jdbcType=CHAR},settlement_interval=#{settlementInterval,jdbcType=VARCHAR},
settlement_type=#{settlementType,jdbcType=VARCHAR},settlement_bank=#{settlementBank,jdbcType=VARCHAR},settlement_card=#{settlementCard,jdbcType=VARCHAR},
settlement_party=#{settlementParty, jdbcType=CHAR},minimum_amount=#{minimumAmount,jdbcType=VARCHAR},maximum_amount=#{maximumAmount,jdbcType=VARCHAR} 
   where id=#{id, jdbcType=BIGINT}
