select * from PMApprate


		and id = #{id, jdbcType=BIGINT}


		and createdate = #{createdate, jdbcType=TIMESTAMP}


		and modifydate = #{modifydate, jdbcType=TIMESTAMP}


		and app_id = #{appId, jdbcType=BIGINT}


		and channel_type = #{channelType, jdbcType=CHAR}


		and rate_type = #{rateType, jdbcType=CHAR}


		and rate = #{rate, jdbcType=DECIMAL}


		and minimum_amount = #{minimumAmount, jdbcType=INTEGER}


		and maximum_amount = #{maximumAmount, jdbcType=INTEGER}


		and settlement_interval = #{settlementInterval, jdbcType=CHAR}


		and modify_user = #{modifyUser, jdbcType=VARCHAR}


LIMIT 2

UPDATE pmapprate SET  modifydate=sysdate(),app_id=#{appId, jdbcType=BIGINT}, channel_type=#{channelType, jdbcType=CHAR}, 
rate_type=#{rateType, jdbcType=CHAR}, rate=#{rate, jdbcType=DECIMAL}, minimum_amount=#{minimumAmount, jdbcType=INTEGER}, 
maximum_amount=#{maximumAmount, jdbcType=INTEGER},modify_user=#{modifyUser, jdbcType=INTEGER}
   where id=#{id, jdbcType=BIGINT}

mapper.insert:
        INSERT INTO `pmappratebak` 
	(`id`,`createdate`, `modifydate`, `app_id`, `channel_type`, `rate_type`, `rate`, `minimum_amount`, `maximum_amount`,`modify_user`) 
	select id,sysdate(),`modifydate`,app_id,channel_type,rate_type,rate,minimum_amount,maximum_amount,modify_user from pmapprate where id =#{id, jdbcType=BIGINT}

mapper.select:
		select p.id,p.modifydate createdate,p.app_id appId,p.channel_type channelType,p.rate_type rateType,p.rate,
		p.minimum_amount minimumAmount,p.maximum_amount maximumAmount,i.name modify_user
 		from pmappratebak p
 		left join inscuser i on i.usercode=p.modify_user


			 	and p.app_id = #{id}



			order by ${sort}  ${order}


			order by createdate desc

		limit #{offset},#{limit}
